package net.gfu.seminar.spring.helloworld.jdbc;import static org.junit.Assert.assertTrue;import java.sql.Connection;import java.sql.SQLException;import javax.sql.DataSource;import net.gfu.seminar.spring.helloworld.config.PersistenceConfig;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.test.annotation.Timed;import org.springframework.test.context.ContextConfiguration;import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;@RunWith(SpringJUnit4ClassRunner.class)@ContextConfiguration(classes = {PersistenceConfig.class})public class DataSourceTest {		@Autowired	private DataSource ds;		@Timed(millis=1000)	@Test public void testDataSourceConnection() throws SQLException{		Connection connection = ds.getConnection();		assertTrue(connection.isValid(100));		connection.close();	}}