package net.gfu.seminar.spring.helloworld.web;import java.util.List;import javax.inject.Inject;import net.gfu.seminar.spring.helloworld.Guest;import net.gfu.seminar.spring.helloworld.GuestDao;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;@Controller@RequestMapping("/guests")public class HelloWorldController {		@Inject	private GuestDao dao;		@RequestMapping(value="/{id}", method = RequestMethod.GET, produces = {"application/json"} )	public @ResponseBody Guest getGuestById(@PathVariable long id) {		return dao.findById(id);	}	@RequestMapping( method = RequestMethod.GET, produces = {"application/json"} )	public @ResponseBody List<Guest> findGuestAll() {		return dao.findAll();	}}