package net.gfu.seminar.spring.helloworld;import org.springframework.core.task.TaskExecutor;public class DoItExecutor {  private TaskExecutor executor;  public DoItExecutor( TaskExecutor executor ) {    this.executor = executor;  }  public void doIt() {    Runnable run = new Runnable() {      public void run() {        System.out.println( Thread.currentThread() );      }    };    executor.execute( run ); executor.execute( run );  }}