package net.gfu.seminar.jaxrs.helloworld;import javax.ws.rs.GET;import javax.ws.rs.Path;import javax.ws.rs.PathParam;import javax.ws.rs.Produces;import javax.ws.rs.QueryParam;import javax.ws.rs.core.MediaType;/** * RESTful controller using Jersey (JAX-RS) API. * @author tf * */@Path("/helloworld")public class HelloWorld {/*	@GET @Produces(MediaType.TEXT_PLAIN)	public String getMessage() {		return "Hello, World!";	}*/	/**	 * http://localhost:8080/helloWorldSpringREST/jaxrs/helloworld	 * @return text/plain	 */	@GET @Produces(MediaType.TEXT_PLAIN)	public String sayPlainTextHello() {		return "Hello, World!";	}		/**	 * http://localhost:8080/helloWorldSpringREST/jaxrs/helloworld	 * Client accept: text/xml	 * @return text/xml	 */	@GET	@Produces(MediaType.TEXT_XML)	public String sayXMLHello() {		return "<?xml version=\"1.0\"?>" + "<hello> Hello, World!" + "</hello>";	}		/**	 * http://localhost:8080/helloWorldSpringREST/jaxrs/helloworld/to?name=Hans	 * @return text/plain	 */	@GET @Produces(MediaType.TEXT_PLAIN) @Path("to")	public String sayPlainTextHelloTo(@QueryParam("name") String name) {		return "Hello, "+name+"!";	}		/**	 * http://localhost:8080/helloWorldSpringREST/jaxrs/helloworld/person/Anna	 * @return text/plain	 */	@GET @Produces(MediaType.TEXT_PLAIN) @Path("person/{name}")	public String sayPlainTextHelloToName(@PathParam("name") String name) {		return "Hello, "+name+"!";	}}